(function () {
    'use strict';

    const clean = (oldData) => {
        var newData = oldData.data.results;
        newData = newData.map((data) => {
            return {
                id : data.id,
                name: data.name || data.title,
                thumbnail: data.thumbnail
            }
        });
        return newData;
        // https://stackoverflow.com/questions/54733622/i-need-remove-unnecessary-json-objects-form-my-result-json-file-using-javascript
    };

    const htmlElement = (jsonData, selector) => {

        for (const comic of jsonData) {
            const className = `.${selector}`;
            const section = document.querySelector(className);
            const article = document.createElement('article');
            const heading = document.createElement('h2');
            const articleImage = document.createElement('img');
            const articleUrl = document.createElement('a');

            articleUrl.textContent = `${comic.hasOwnProperty('name') ? comic.name : ''}`;

            articleImage.src = `${comic.thumbnail.path}.${comic.thumbnail.extension}`; //template literals
            articleUrl.href = `#${selector}/${comic.id}`;

            heading.appendChild(articleUrl);
            article.appendChild(articleImage);
            article.appendChild(heading);
            section.appendChild(article);
        }
    };

    const baseUrl = new URL('https://gateway.marvel.com/v1/public/');
    const comicsEndpoint = new URL('comics', baseUrl);
    const charactersEndpoint = new URL('characters', baseUrl);

    const queries = {
        dateDescriptor: 'thisMonth',
        orderBy: 'onsaleDate',
        limit : 10,
        ts : '1581025873',
        apikey : '22b5f2403c91db4fba23cad90a8b2ab7',
        hash : 'e6bb9dbff35775d2d8aed171d44888d4'

    };

    const searchParams = new URLSearchParams(queries);
    comicsEndpoint.search = searchParams;

    const searchParamsCharacters = new URLSearchParams(queries);
    charactersEndpoint.search = searchParamsCharacters;

    // const getJsonData getAllComics
    const getAllData = (url) => {
        console.log(url);
        return fetch(url)
            .then(response => {
                return response.json();
            }).then(jsonData => {
                const cleanedData = clean(jsonData);
                console.log(cleanedData);
                // const test = cleanedData.sort( (a,b)=> a.name.localeCompare(b.name));
                // console.log(test);
                // https://stackoverflow.com/questions/6712034/sort-array-by-firstname-alphabetically-in-javascript/16481400#16481400
                return cleanedData;
            })

    };

    const allComics = () => {
        getAllData(comicsEndpoint).then(data => {
            htmlElement(data, 'comics');
        });
    };

    const getComic = (id) => {
        getAllData(comicsEndpoint).then(data => {
            data = data.filter(data => data.id == id);
            return data;
        }).then(data => {
            htmlElement(data, 'comic-detail');
        });
        // var detail = data.filter(data => data.id == id);
        // detail = detail[0];
        // https://medium.com/poka-techblog/simplify-your-javascript-use-map-reduce-and-filter-bd02c593cc2d
    };

    // fetch data for overview page
    // but also for detailpage
    // check if the data already exist if not fetch data

    const routes = () => {
        routie({
            '': function () {
                allComics();
            },
            'characters': function () {
                allComics();
            },
            'comics': function () {
                allComics();
            },
            'comics/:id': function (id) {
                getComic(id);
            }
        });
    };

    routes();
    // https://superheroapi.com/api/1776314525838688/search/

}());
